import React, { Component } from 'react';
import {
  Container,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Box,
  Stack,
} from '@mui/material';

class DatabaseDemo extends Component {
  constructor(props) {
    super(props);
    this.handleTextChange = this.handleTextChange.bind(this);
    this.handleButtonClick = this.handleButtonClick.bind(this);
    this.handleButtonClickDel = this.handleButtonClickDel.bind(this);
    this.state = {
      movies: [],
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
    };
  }

  componentDidMount() {
    this.populateData();
  }

  populateData() {
    this.fetch_retry('/api/transaction', 3)
      .then(res => res.json())
      .then((data) => {
        this.setState({ movies: data });
      })
      .catch(console.log);
  }

  async fetch_retry(url, n) {
    try {
      return await fetch(url);
    } catch (err) {
      if (n === 1) throw err;
      await new Promise(resolve => setTimeout(resolve, 1000));
      return await this.fetch_retry(url, n - 1);
    }
  }

  renderTableData() {
    return this.state.movies.map((movie, index) => {
      const { name, posterImg, description, rating, year, genres } = movie;
      return (
        <TableRow key={index}>
          <TableCell>{name}</TableCell>
          <TableCell>
            <img
              src={posterImg}
              alt={name}
              style={{ width: '50px', borderRadius: '4px' }}
            />
          </TableCell>
          <TableCell>{description}</TableCell>
          <TableCell>{rating}</TableCell>
          <TableCell>{year}</TableCell>
          <TableCell>{genres.join(", ")}</TableCell>
        </TableRow>
      );
    });
  }

  handleButtonClickDel() {
    const requestOptions = {
      method: 'DELETE',
    };
    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData());

    this.setState({
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
      movies: [],
    });
  }

  handleButtonClick() {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        name: this.state.name,
        posterImg: this.state.posterImg,
        coverImg: this.state.coverImg,
        description: this.state.description,
        rating: this.state.rating,
        year: this.state.year,
        tagLine: this.state.tagLine,
        minutes: this.state.minutes,
        genres: this.state.genres.split(",").map(genre => genre.trim()),
      }),
    };

    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData());

    this.setState({
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
    });
  }

  handleTextChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    return (
      <Container maxWidth="md" style={{ marginTop: '20px' }}>
        <Typography variant="h4" component="h1" gutterBottom>
          ðŸŽ¥ Movie Database Manager
        </Typography>

        <Box mb={4}>
          <Stack spacing={2}>
            <TextField
              label="Movie Name"
              name="name"
              value={this.state.name}
              onChange={this.handleTextChange}
              fullWidth
            />
            <TextField
              label="Poster URL"
              name="posterImg"
              value={this.state.posterImg}
              onChange={this.handleTextChange}
              fullWidth
            />
            <TextField
              label="Description"
              name="description"
              value={this.state.description}
              onChange={this.handleTextChange}
              fullWidth
            />
            <Stack direction="row" spacing={2}>
              <TextField
                label="Rating (1-10)"
                name="rating"
                value={this.state.rating}
                onChange={this.handleTextChange}
                fullWidth
              />
              <TextField
                label="Year"
                name="year"
                value={this.state.year}
                onChange={this.handleTextChange}
                fullWidth
              />
            </Stack>
            <TextField
              label="Genres (comma-separated)"
              name="genres"
              value={this.state.genres}
              onChange={this.handleTextChange}
              fullWidth
            />
            <Stack direction="row" spacing={2}>
              <Button
                variant="contained"
                color="primary"
                onClick={this.handleButtonClick}
              >
                Add Movie
              </Button>
              <Button
                variant="outlined"
                color="error"
                onClick={this.handleButtonClickDel}
              >
                Delete All
              </Button>
            </Stack>
          </Stack>
        </Box>

        <Typography variant="h5" component="h2" gutterBottom>
          Movies List
        </Typography>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>Poster</TableCell>
                <TableCell>Description</TableCell>
                <TableCell>Rating</TableCell>
                <TableCell>Year</TableCell>
                <TableCell>Genres</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>{this.renderTableData()}</TableBody>
          </Table>
        </TableContainer>
      </Container>
    );
  }
}

export default DatabaseDemo;
