import { useState, useEffect } from 'react';

const TransactionDashboard = () => {
  const [transactions, setTransactions] = useState([]);
  const [formData, setFormData] = useState({ amount: '', description: '' });
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    populateData();
  }, []);

  const populateData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const data = await fetchWithRetry('/api/transaction', 3);
      setTransactions(data.result);
    } catch (err) {
      setError('Failed to load transactions. Please try again.');
      console.error('Error fetching transactions:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchWithRetry = async (url, retries) => {
    for (let i = 0; i < retries; i++) {
      try {
        const response = await fetch(url);
        if (!response.ok) throw new Error(`HTTP error: ${response.status}`);
        return await response.json();
      } catch (err) {
        if (i === retries - 1) throw err;
        await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddTransaction = async () => {
    if (!formData.amount || !formData.description) {
      setError('Please fill in both amount and description.');
      return;
    }

    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/transaction', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          amount: formData.amount,
          desc: formData.description
        })
      });

      if (!response.ok) throw new Error('Failed to add transaction');

      await populateData();
      setFormData({ amount: '', description: '' });
    } catch (err) {
      setError('Failed to add transaction. Please try again.');
      console.error('Error adding transaction:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteAll = async () => {
    if (!window.confirm('Are you sure you want to delete all transactions?')) {
      return;
    }

    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/transaction', {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('Failed to delete transactions');

      await populateData();
    } catch (err) {
      setError('Failed to delete transactions. Please try again.');
      console.error('Error deleting transactions:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">
          Transaction Dashboard
        </h1>
        <button
          onClick={handleDeleteAll}
          className="px-4 py-2 text-red-600 border border-red-600 rounded hover:bg-red-50"
          disabled={isLoading}
        >
          Delete All
        </button>
      </div>

      {error && (
        <div className="mb-4 p-4 bg-red-50 text-red-600 rounded">
          {error}
        </div>
      )}

      <div className="mb-6 p-4 bg-white rounded shadow">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Amount
            </label>
            <input
              type="number"
              name="amount"
              value={formData.amount}
              onChange={handleInputChange}
              className="w-full p-2 border rounded"
              placeholder="Enter amount"
              disabled={isLoading}
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <input
              type="text"
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              className="w-full p-2 border rounded"
              placeholder="Enter description"
              disabled={isLoading}
            />
          </div>
        </div>
        <button
          onClick={handleAddTransaction}
          className="mt-4 w-full py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          disabled={isLoading}
        >
          Add Transaction
        </button>
      </div>

      <div className="bg-white rounded shadow overflow-hidden">
        <table className="w-full">
          <thead>
            <tr className="bg-gray-50">
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                ID
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Amount
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Description
              </th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {transactions.map(({ id, amount, description }) => (
              <tr key={id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {id}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  ${Number(amount).toFixed(2)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {description}
                </td>
              </tr>
            ))}
            {transactions.length === 0 && !isLoading && (
              <tr>
                <td colSpan="3" className="px-6 py-4 text-center text-gray-500">
                  No transactions found
                </td>
              </tr>
            )}
            {isLoading && (
              <tr>
                <td colSpan="3" className="px-6 py-4 text-center text-gray-500">
                  Loading...
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TransactionDashboard;
