import React, { Component } from 'react';
import './DatabaseDemo.css';

class DatabaseDemo extends Component {
  constructor(props) {
    super(props);
    this.handleTextChange = this.handleTextChange.bind(this);
    this.handleButtonClick = this.handleButtonClick.bind(this);
    this.handleButtonClickDel = this.handleButtonClickDel.bind(this);
    this.state = {
      movies: [],
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
    };
  }

  componentDidMount() {
    this.populateData();
  }

  populateData() {
    this.fetch_retry('/api/transaction', 3)
      .then(res => res.json())
      .then((data) => {
        this.setState({ movies: data });
      })
      .catch(console.log);
  }

  async fetch_retry(url, n) {
    try {
      return await fetch(url);
    } catch (err) {
      if (n === 1) throw err;
      await new Promise(resolve => setTimeout(resolve, 1000));
      return await this.fetch_retry(url, n - 1);
    }
  }

  renderTableData() {
    return this.state.movies.map((movie, index) => {
      const { name, posterImg, description, rating, year, genres } = movie;
      return (
        <tr key={index}>
          <td>{name}</td>
          <td><img src={posterImg} alt={name} className="movie-poster" /></td>
          <td>{description}</td>
          <td>{rating}</td>
          <td>{year}</td>
          <td>{genres.join(", ")}</td>
        </tr>
      );
    });
  }

  handleButtonClickDel() {
    const requestOptions = {
      method: 'DELETE',
    };
    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData());

    this.setState({
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
      movies: [],
    });
  }

  handleButtonClick() {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        name: this.state.name,
        posterImg: this.state.posterImg,
        coverImg: this.state.coverImg,
        description: this.state.description,
        rating: this.state.rating,
        year: this.state.year,
        tagLine: this.state.tagLine,
        minutes: this.state.minutes,
        genres: this.state.genres.split(",").map(genre => genre.trim()),
      }),
    };

    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData());

    this.setState({
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
    });
  }

  handleTextChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    return (
      <div className="database-demo">
        <h1 className="title">Movies Database</h1>
        <div className="form-container">
          <div className="form-row">
            <input type="text" name="name" value={this.state.name} onChange={this.handleTextChange} placeholder="Name" />
            <input type="text" name="posterImg" value={this.state.posterImg} onChange={this.handleTextChange} placeholder="Poster URL" />
            <input type="text" name="description" value={this.state.description} onChange={this.handleTextChange} placeholder="Description" />
          </div>
          <div className="form-row">
            <input type="text" name="rating" value={this.state.rating} onChange={this.handleTextChange} placeholder="Rating" />
            <input type="text" name="year" value={this.state.year} onChange={this.handleTextChange} placeholder="Year" />
            <input type="text" name="genres" value={this.state.genres} onChange={this.handleTextChange} placeholder="Genres (comma-separated)" />
          </div>
          <div className="form-row">
            <button className="btn add-btn" onClick={this.handleButtonClick}>Add Movie</button>
            <button className="btn del-btn" onClick={this.handleButtonClickDel}>Delete All</button>
          </div>
        </div>
        <table className="movies-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Poster</th>
              <th>Description</th>
              <th>Rating</th>
              <th>Year</th>
              <th>Genres</th>
            </tr>
          </thead>
          <tbody>
            {this.renderTableData()}
          </tbody>
        </table>
      </div>
    );
  }
}

export default DatabaseDemo;
