import React, { Component } from 'react';
import {
  TextField,
  Button,
  Typography,
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Box,
  Grid,
  ThemeProvider,
  createTheme,
} from '@mui/material';

const lightTheme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2', // Bright blue
    },
    secondary: {
      main: '#ff4081', // Vibrant pink
    },
    error: {
      main: '#d32f2f', // Bright red
    },
    background: {
      default: '#ffffff', // White background
      paper: '#f9f9f9', // Light grey for cards/paper
    },
    text: {
      primary: '#000000', // Black text
      secondary: '#4f4f4f', // Dark grey text
    },
  },
  typography: {
    fontFamily: 'Arial, sans-serif',
  },
});

class DatabaseDemo extends Component {
  constructor(props) {
    super(props);
    this.handleTextChange = this.handleTextChange.bind(this);
    this.handleButtonClick = this.handleButtonClick.bind(this);
    this.handleButtonClickDel = this.handleButtonClickDel.bind(this);
    this.state = {
      movies: [],
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
    };
  }

  componentDidMount() {
    this.populateData();
  }

  populateData() {
    this.fetch_retry('/api/transaction', 3)
      .then(res => res.json())
      .then((data) => {
        this.setState({ movies: data });
      })
      .catch(console.log);
  }

  async fetch_retry(url, n) {
    try {
      return await fetch(url);
    } catch (err) {
      if (n === 1) throw err;
      await new Promise(resolve => setTimeout(resolve, 1000));
      return await this.fetch_retry(url, n - 1);
    }
  }

  renderTableData() {
    return this.state.movies.map((movie, index) => {
      const { name, posterImg, description, rating, year, genres } = movie;
      return (
        <TableRow key={index}>
          <TableCell>{name}</TableCell>
          <TableCell>
            <img src={posterImg} alt={name} style={{ width: 100, height: 'auto' }} />
          </TableCell>
          <TableCell>{description}</TableCell>
          <TableCell>{rating}</TableCell>
          <TableCell>{year}</TableCell>
          <TableCell>{genres.join(", ")}</TableCell>
        </TableRow>
      );
    });
  }

  handleButtonClickDel() {
    const requestOptions = {
      method: 'DELETE',
    };
    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData());

    this.setState({
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
      movies: [],
    });
  }

  handleButtonClick() {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        name: this.state.name,
        posterImg: this.state.posterImg,
        coverImg: this.state.coverImg,
        description: this.state.description,
        rating: this.state.rating,
        year: this.state.year,
        tagLine: this.state.tagLine,
        minutes: this.state.minutes,
        genres: this.state.genres.split(",").map(genre => genre.trim()),
      }),
    };

    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData());

    this.setState({
      name: "",
      posterImg: "",
      coverImg: "",
      description: "",
      rating: "",
      year: "",
      tagLine: "",
      minutes: "",
      genres: "",
    });
  }

  handleTextChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    return (
      <ThemeProvider theme={lightTheme}>
        <Container>
          <Typography variant="h3" align="center" gutterBottom>
            ðŸŽ¥ Movie Database Manager
          </Typography>
          <Box sx={{ marginY: 4 }}>
            <Typography variant="h5" gutterBottom>
              Add a New Movie
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Movie Name"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleTextChange}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Poster URL"
                  name="posterImg"
                  value={this.state.posterImg}
                  onChange={this.handleTextChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Description"
                  name="description"
                  value={this.state.description}
                  onChange={this.handleTextChange}
                />
              </Grid>
              <Grid item xs={6} md={4}>
                <TextField
                  fullWidth
                  label="Rating"
                  name="rating"
                  value={this.state.rating}
                  onChange={this.handleTextChange}
                />
              </Grid>
              <Grid item xs={6} md={4}>
                <TextField
                  fullWidth
                  label="Year"
                  name="year"
                  value={this.state.year}
                  onChange={this.handleTextChange}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Genres (comma-separated)"
                  name="genres"
                  value={this.state.genres}
                  onChange={this.handleTextChange}
                />
              </Grid>
            </Grid>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: 2 }}>
              <Button variant="contained" color="primary" onClick={this.handleButtonClick}>
                Add Movie
              </Button>
              <Button variant="outlined" color="error" onClick={this.handleButtonClickDel}>
                Delete All
              </Button>
            </Box>
          </Box>
          <Box sx={{ marginY: 4 }}>
            <Typography variant="h5" gutterBottom>
              Movies List
            </Typography>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Name</TableCell>
                    <TableCell>Poster</TableCell>
                    <TableCell>Description</TableCell>
                    <TableCell>Rating</TableCell>
                    <TableCell>Year</TableCell>
                    <TableCell>Genres</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>{this.renderTableData()}</TableBody>
              </Table>
            </TableContainer>
          </Box>
        </Container>
      </ThemeProvider>
    );
  }
}

export default DatabaseDemo;
