import React, { Component } from 'react';
import './DatabaseDemo.css';

class DatabaseDemo extends Component {

  constructor(props) {
    super(props); // Calling the constructor of the parent class
    this.handleTextChange = this.handleTextChange.bind(this);
    this.handleButtonClick = this.handleButtonClick.bind(this);
    this.handleButtonClickDel = this.handleButtonClickDel.bind(this);
    this.state = {
      transactions: [],
      text_amt: "",
      text_desc: ""
    };
  }

  componentDidMount() {
    this.populateData();
  }

  // Fetch data with retry logic
  populateData() {
    this.fetch_retry('/api/transaction', 3)
      .then(res => res.json())
      .then(data => {
        this.setState({ transactions: data.result });
      })
      .catch(err => console.error('Error fetching transactions:', err));
  }

  // Retry mechanism for fetch
  async fetch_retry(url, n) {
    try {
      return await fetch(url);
    } catch (err) {
      if (n === 1) throw err;
      await new Promise(resolve => setTimeout(resolve, 1000)); // Wait before retrying
      return await this.fetch_retry(url, n - 1);
    }
  }

  // Render table data
  renderTableData() {
    return this.state.transactions.map((transaction, index) => {
      const { id, amount, description } = transaction;
      return (
        <tr key={id}>
          <td>{id}</td>
          <td>{amount}</td>
          <td>{description}</td>
        </tr>
      );
    });
  }

  // Handle deleting all transactions
  handleButtonClickDel() {
    const requestOptions = { method: 'DELETE' };
    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData())
      .catch(err => console.error('Error deleting transactions:', err));

    // Clear input fields after deletion
    this.setState({ text_amt: "", text_desc: "" });
  }

  // Handle adding a new transaction
  handleButtonClick() {
    const { text_amt, text_desc } = this.state;
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ amount: text_amt, desc: text_desc })
    };

    fetch('/api/transaction', requestOptions)
      .then(response => response.json())
      .then(() => this.populateData())
      .catch(err => console.error('Error adding transaction:', err));

    // Clear input fields after adding
    this.setState({ text_amt: "", text_desc: "" });
  }

  // Handle input changes
  handleTextChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    const { text_amt, text_desc, transactions } = this.state;

    return (
      <div>
        <h1 id='title' style={{ paddingRight: "1em" }}>
          Aurora Database Demo Page
        </h1>
        <input
          style={{ float: "right", marginBottom: "1em" }}
          type="button"
          value="DEL"
          onClick={this.handleButtonClickDel}
        />
        <table id='transactions'>
          <tbody>
            <tr>
              <td>ID</td>
              <td>AMOUNT</td>
              <td>DESC</td>
            </tr>
            <tr>
              <td><input type="button" value="ADD" onClick={this.handleButtonClick} /></td>
              <td>
                <input
                  type="text"
                  name="text_amt"
                  value={text_amt}
                  onChange={this.handleTextChange}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="text_desc"
                  value={text_desc}
                  onChange={this.handleTextChange}
                />
              </td>
            </tr>
            {this.renderTableData()}
          </tbody>
        </table>
      </div>
    );
  }
}

export default DatabaseDemo;
